import {
  useState,
  ChangeEvent,
  KeyboardEvent,
  ClipboardEvent,
  useCallback,
  useMemo,
} from 'react';
import { useQuaxDispatch, useQuaxSelector } from './quax/useQuaxStore';
import { Value } from './components/Value';
import { Button } from './components/Button';
import { createCancelAllOperationsAction } from './ducks/actions';
import './components/App/App.css';

export default function App() {
  const dispatch = useQuaxDispatch();
  const [value, setValue] = useState('');
  const isLoading = useQuaxSelector((state) => state.loading);

  const setCounterByValue = useCallback((actionType: 'inc' | 'dec') => () => {
    if (!value || isNaN(+value) || +value <= 0) {
      return;
    }
    if (actionType === 'inc') {
      dispatch({
        type: 'INCREMENT_BY_VALUE_ASYNC',
        payload: +value
      });
    } else {
      dispatch({
        type: 'DECREMENT_BY_VALUE_ASYNC',
        payload: +value
      });
    }
  }, [dispatch, value]);

  const handleKeyDown = useCallback((e: KeyboardEvent<HTMLInputElement>) => {
    if (['e', 'E', '+', '-', '.'].includes(e.key)) {
      e.preventDefault();
    }
  }, []);

  const handlePaste = useCallback((e: ClipboardEvent<HTMLInputElement>) => {
    const pasteData = e.clipboardData.getData('text');
    if (/\D/.test(pasteData)) {
      e.preventDefault();
    }
  }, []);

  const handleChange = useCallback((e: ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value.replace(/\D/g, '');
    setValue(newValue);
  }, []);

  const clickHandler = useCallback((actionType: 'inc' | 'dec') => () => {
    if (actionType === 'inc') {
      dispatch({
        type: 'INCREMENT_ASYNC'
      });
    } else {
      dispatch({
        type: 'DECREMENT_ASYNC'
      });
    }
  }, [dispatch]);

  const cancelOperation = useCallback(() => {
    dispatch(createCancelAllOperationsAction());
  }, [dispatch]);

  const incrementButton = useMemo(() => (
    <Button text="Увеличить" onClick={clickHandler('inc')} />
  ), [clickHandler]);

  const decrementButton = useMemo(() => (
    <Button text="Уменьшить" onClick={clickHandler('dec')} />
  ), [clickHandler]);

  const isInputInvalid = !value || isNaN(+value) || +value <= 0;

  const incrementByValueButton = useMemo(() => (
    <Button
      text="Увеличить на значение"
      onClick={setCounterByValue('inc')}
      className={isInputInvalid ? 'disabled' : ''}
    />
  ), [setCounterByValue, isInputInvalid]);

  const decrementByValueButton = useMemo(() => (
    <Button
      text="Уменьшить на значение"
      onClick={setCounterByValue('dec')}
      className={isInputInvalid ? 'disabled' : ''}
    />
  ), [setCounterByValue, isInputInvalid]);

  const cancelButton = useMemo(() => (
    <Button
      text="Отменить операцию"
      onClick={cancelOperation}
      className={isLoading ? '' : 'disabled'}
    />
  ), [cancelOperation, isLoading]);

  return (
    <div className="app">
      <Value />
      <div className="controls">
        {incrementButton}
        {decrementButton}
      </div>
      <div className="input-container">
        <input
          className="input"
          placeholder="изменить на значение"
          type="text"
          value={value}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
          onPaste={handlePaste}
          inputMode="numeric"
          pattern="[0-9]*"
          aria-label="Введите число"
        />
        <div className="button-group">
          {incrementByValueButton}
          {decrementByValueButton}
        </div>
      </div>
      {cancelButton}
    </div>
  );
}
