import React from 'react';
import { useQuaxSelector } from '../../quax/useQuaxStore';
import { Loader } from '../Loader/Loader';
import './Button.css';

export interface ButtonProps {
  text: string;
  onClick: () => void;
  disabled?: boolean;
  className?: string;
}

const Button: React.FC<ButtonProps> = ({
  text,
  onClick,
  disabled = false,
  className = ''
}) => {
  const isLoading = useQuaxSelector((state) => state.loading);

  const buttonClassName = `button ${className}`.trim();
  const isDisabled = disabled || isLoading;

  return (
    <button
      className={buttonClassName}
      onClick={onClick}
      disabled={isDisabled}
      aria-busy={isLoading}
    >
      {text}

    </button>
  );
};

export { Button };
