import {
  put,
  take,
  takeEvery,
  race,
  fork,
  ForkEffect,
  select,
  call,
  SelectEffect,
  CallEffect,
  PutEffect,
  cancelled,
  RaceEffect,
  TakeEffect,
} from 'redux-saga/effects';
import {
  createOperationSuccessAction,
  createIncrementByValueAction,
  createDecrementByValueAction,
  createLoadingEndAction,
  createCancelAllOperationsAction,
  AppAction
} from './actions';
import { ActionTypes } from './actionTypes';
import { makeOperation } from '../api';
import { AppState } from './types';

type SagaEffect =
  | SelectEffect
  | CallEffect<number>
  | PutEffect<AppAction>
  | RaceEffect<{
      operation: CallEffect<void>;
      cancel: TakeEffect;
    }>;

const selectValue: (state: AppState) => number = (state) => state.value;

function* handleOperation(currentValue: number, delta: number): Generator<SagaEffect, void, number> {
  try {
    const result = yield call(makeOperation, currentValue, delta);
    yield put(createOperationSuccessAction(result));
  } finally {
    if (yield cancelled()) {
      console.log('Operation was cancelled');
    }
    yield put(createLoadingEndAction());
  }
}

function* handleIncrement(): Generator<SagaEffect, void, number> {
  const currentValue = yield select(selectValue);
  yield race({
    operation: call(handleOperation, currentValue, 1),
    cancel: take(ActionTypes.CANCEL_ALL_OPERATIONS)
  });
}

function* handleDecrement(): Generator<SagaEffect, void, number> {
  const currentValue = yield select(selectValue);
  yield race({
    operation: call(handleOperation, currentValue, -1),
    cancel: take(ActionTypes.CANCEL_ALL_OPERATIONS)
  });
}

function* handleIncrementByValue(action: ReturnType<typeof createIncrementByValueAction>): Generator<SagaEffect, void, number> {
  const currentValue = yield select(selectValue);
  yield race({
    operation: call(handleOperation, currentValue, action.payload),
    cancel: take(ActionTypes.CANCEL_ALL_OPERATIONS)
  });
}

function* handleDecrementByValue(action: ReturnType<typeof createDecrementByValueAction>): Generator<SagaEffect, void, number> {
  const currentValue = yield select(selectValue);
  yield race({
    operation: call(handleOperation, currentValue, -action.payload),
    cancel: take(ActionTypes.CANCEL_ALL_OPERATIONS)
  });
}

export function* rootSaga() {
  yield takeEvery(ActionTypes.INCREMENT_ASYNC, handleIncrement);
  yield takeEvery(ActionTypes.DECREMENT_ASYNC, handleDecrement);
  yield takeEvery(ActionTypes.INCREMENT_BY_VALUE_ASYNC, handleIncrementByValue);
  yield takeEvery(ActionTypes.DECREMENT_BY_VALUE_ASYNC, handleDecrementByValue);
}
