export const makeOperation = (
  value: number,
  additional: number,
  signal?: AbortSignal
): Promise<number> =>
  new Promise<number>((resolve, reject) => {
    const isOk = true; //Math.random() > 0.9; зачем это так и не понял???
    setTimeout(() => {
      if (isOk) {
        resolve(value + additional);
      } else {
        reject(new Error('Ой что-то беку поплохело'));
      }
    }, 5000);

    // Если снаружи вызовут abort(), прерываем операцию
    if (signal) {
      signal.addEventListener('abort', () => {
        clearTimeout(timeoutId);
        reject(new Error('Операция отменена'));
      });
    }
  });
